import React, { useState } from 'react'

function Tour(props) {

    const   [data,setData]  = useState(props.data);
    const   [readMore,setReadmore] = useState(false);

 const handleClick = () => {
        setReadmore(!readMore)
    }

  return (
    <div>
        {
            data.map((city,id) => (

                <article key={id}>
                    <div className="box-img">
                        <img src={city.img} alt={city.name} />
                    </div>
                    <div className="content">
                        <div className="text-tour">
                            <h1>{city.name}</h1>
                            <hr />
                            <p>{readMore ? `${city.desc.substring(0)}` : `${city.desc.substring(0,150)}...`}</p>

                            <button className='read-more' onClick={()=>handleClick()}>read more</button>
                        </div>
                        <div className="price" title='Price'>{city.price}</div>
                    </div>

                    <button className='close-tour' title='Not Interested' >X</button>
                </article>
            ))
        }
    </div>
  )
}


export default Tour
